export default {

	increment: (item, name, article, price) => {
  const cart = main.store["cart"] || [];

  const cartItem = cart.find((e) => Object.keys(e)[0] === item);

  if (cartItem) {
    const newArr = cart.map((e) => {
      if (Object.keys(e)[0] === item) {
        return { [item]: {
					_id: item,
				name:name,
					article:article,
					price: price,
					amount:e[item].amount + 1} };
      }
      return e;
    });
    storeValue("cart", newArr, false);
  } else {
    const newItem = { [item]:{ 
				_id: item,
				name:name,
					article:article,
			price: price,
			   amount: 1 } } ;
    storeValue("cart", [...cart, newItem], false);
  }
},
decrement: (item, name, article, price)=>{


  const cart = main.store["cart"] || [];

  const cartItem = cart.find((e) => Object.keys(e)[0] === item);

  if (cartItem) {
    const newArr = cart.map((e) => {
      if (Object.keys(e)[0] === item) {
        return { [item]: {
						_id: item,
					name:name,
						article:article,
					price: price,
					amount:e[item].amount - 1 }};
      }
      return e;
    });
    storeValue("cart", newArr, false);
  } else {
    const newItem = { [item]: {
				_id: item,
					name:name,
				article:article,
			price: price,
		    	amount:0} };
    storeValue("cart", [...cart, newItem], false);
  }
},

cleanStore: ()=>{
	storeValue("cart",undefined,false);
storeValue("components",undefined,false)
}


