jsonPathKeys:
(item)=>{
    let cart = [];
    // если корзина пуста
    if(!main.store["cart"]){
        let data= {item, amountItem: 0}
        cart.push(data);
        storeValue("cart", cart, false)
    }	

    // если элемент уже есть в корзине
    if(main.store["cart"]){
        const cartItem = main.store["cart"].find(e => e.item=== item);
        // если элемент уже есть в корзине, и мы хотим уменьшить количество 
        if(cartItem){
            const newArr = main.store["cart"].map(e => {
                if (e === cartItem) {
                    return {...e, amountItem: e.amountItem - 1} 
                } else {
                    return e
                }
            })
            storeValue("cart", newArr, false)
        } else {
            storeValue('cart', [...main.store['cart'], {item, amountItem: 1}], false)											
        }
    }
}

